    0%, 20%, 50%, 80%, 100% {
        transform: translateY(0); /* Start and end with no vertical translation */
    }
    40% {
        transform: translateY(-20px); /* Bounce upwards at 40% */
    }
    60% {
        transform: translateY(-10px); /* Bounce downwards at 60% */
    }
}
